import React from "react";
import logo from "./Sanjivani.png";
import styled from "styled-components";
import { useState } from "react";
import "react-slideshow-image/dist/styles.css";
import { Slide } from "react-slideshow-image";
import Navbar from "../Navbar/Navbar";
import {
  MDBCollapse,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarNav,
  MDBNavbarItem,
  MDBNavbarLink,
  MDBNavbarToggler,
  MDBContainer,
  MDBIcon,
  MDBInput,
  MDBBtn,
} from "mdb-react-ui-kit";
import ARVR from "./images/AR-VR-Dev.jpg";
import BlockChain from "./images/blockchain-development.webp";
import webDev from "./images/WebDevlopment.jpg";
import dataSci from "./images/data-science.jpg";
import embeddedC from "./images/Embedded-c-programming.jpg";
import Programming from "./images/programming-language.png";
import Assembly from "./images/Assembly.avif";
import AppointmentFormSoftware from "../components/FabLab/AppointmentFormSoftware";
export default function Software() {
  const [showBasic, setShowBasic] = useState(false);

  // const spanStyle = {
  //   padding: '20px',
  //   background: '#efefef',
  //   color: '#000000'
  // }

  const divStyle = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#84CEEB", // Change to your desired background color
    backgroundSize: "contain",
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center",
    height: "600px",
    width: "100%",
  };

  const slideImages = [
    {
      url: dataSci,
      //caption: 'Image 1'
    },
    {
      url: webDev,
      // caption: 'Image 2'
    },
    {
      url: Programming,
      // caption: 'Image 3'
    },
    {
      url: BlockChain,
      // caption: 'Image 3'
    },
    {
      url: embeddedC,
      // caption: 'Image 3'
    },
    {
      url: ARVR,
      // caption: 'Image 3'
    },
    {
      url: Assembly,
      // caption: 'Image 3'
    },
  ];
  return (
    <>
      <MDBNavbar
        style={{ backgroundColor: "white" }}
        className="nav"
        expand="lg"
        light
        bgColor="#5AB9EA"
      >
        <MDBContainer fluid>
          <img
            src={logo}
            style={{ height: "50px", width: "65px", padding: "5px" }}
          />

          <MDBNavbarBrand
            style={{
              paddingLeft: "10px",
              fontFamily: "Nexa",
              fontSize: "30px",
            }}
            href="/landing"
          >
            Mechtrix Consultants
          </MDBNavbarBrand>

          <MDBNavbarToggler
